import { useState } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);

  const [enteredAge, setEnteredAge] = useState("");
  const [enteredAgeTouched, setEnteredAgeTouched] = useState(false);

  const enteredNameIsValid = enteredName.trim() !== "";
  const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched;

  const enteredAgeIsValid = enteredAge.trim() !== "";
  const ageInputIsInvalid = !enteredAgeIsValid && enteredAgeTouched;

  let formIsValid = false;

  if (enteredNameIsValid && enteredAgeIsValid) {
    formIsValid = true;
  }

  const nameChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const nameBlurHandler = () => {
    setEnteredNameTouched(true);
  };

  const ageChangeHandler = (event) => {
    setEnteredAge(event.target.value);
  };

  const ageBlurHandler = () => {
    setEnteredAgeTouched(true);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    setEnteredNameTouched(true);
    setEnteredAgeTouched(true);
    if (!enteredNameIsValid || !enteredAgeIsValid) {
      return;
    }

    console.log(enteredName);
    setEnteredName("");
    setEnteredAge("");
    setEnteredNameTouched(false);
    setEnteredAgeTouched(false);
  };

  const nameInputClasses =
    nameInputIsInvalid || ageInputIsInvalid
      ? "form-control invalid"
      : "form-control";

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameChangeHandler}
          onBlur={nameBlurHandler}
          value={enteredName}
        />
        {nameInputIsInvalid && (
          <p className="error-text">Name must not be empty.</p>
        )}
      </div>
      <div className={nameInputClasses}>
        <label htmlFor="age">Your Age</label>
        <input
          type="number"
          id="age"
          onChange={ageChangeHandler}
          onBlur={ageBlurHandler}
          value={enteredAge}
        />
        {ageInputIsInvalid && (
          <p className="error-text">Age must not be empty.</p>
        )}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
